package date13032012;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Simple assignment
		int x;
		x=100;
		x+=10;
		System.out.println("x: "+x);
		x-=10;
		System.out.println("x: "+x);
		x*=10;
		System.out.println("x: "+x);
		x/=10;
		System.out.println("x: "+x);
		x%=3;
		System.out.println("x: "+x);
		
		
	}

}
